<template>
  <section class="container">
    <header class="logo"></header>
    <form class="form-body" @submit.prevent="doLogin">
      <div class="input_label">
        <i class="iconfont icon-account"></i>
        <input type="text" placeholder="请输入账号" class="input_text" v-model="username">
      </div>
      <div class="input_label">
        <i class="iconfont icon-password"></i>
        <input type="password" placeholder="请输入密码" class="input_text" v-model="password">
      </div>
      <div class="input_label btn_group">
        <input type="submit"  class="btn" value="登 录">  
        <!-- <button class="btn"> 登 录 </button> -->
      </div>
    </form>
  </section>
</template>
<script>
import store from '@/common/js/fn'
export default {
  data () {
    return {
      username: '',
      password: ''
    }
  },
  methods: {
    doLogin () {
      this.$ajx.queryLogin({
        // username: this.username,
        // password: this.password
        username: '铁三院',
        password: '123456'        
      }).then(res => {
        if (res.err === 0) {
          store.setItem('PGINFO', JSON.stringify({
            token: res.token,
            name: res.data[0].TrueName
          }))
          
          this.$router.push({path: '/'})
        } else {
          console.log(res.err)
        }
      }).catch(e => {
        // throw new Error('返回错误')
      })
      // this.$ajx.queryCate({
      // }).then(res => {
      //   console.log(res)
      // })      
    }
  }
}
</script>
